package integrity

import (
	"encoding/hex"
	"math/big"
	"os"

	"github.com/ethereum/go-ethereum/accounts/abi"
	"github.com/ethereum/go-ethereum/common"
	"github.com/iotexproject/go-pkgs/crypto"
)

var (
	_ERC20contractByteCode = ""

	opMultiSend = getMultiSendData(100)
)

func getMultiSendData(num int) []byte {
	f, err := os.Open("erc20_multisend.abi")
	if err != nil {
		panic(err)
	}
	defer f.Close()

	multiSendABI, err := abi.JSON(f)
	if err != nil {
		panic(err)
	}

	addrs := []common.Address{}
	for i := 0; i < num; i++ {
		addr, err := crypto.GenerateKey()
		if err != nil {
			panic(err)
		}
		addrs = append(addrs, common.BytesToAddress(addr.Bytes()))
	}
	value := big.NewInt(10)
	data, err := multiSendABI.Pack("multiTransfer", addrs, value)
	if err != nil {
		panic(err)
	}
	return data
}

var (
	_contractByteCode = ""

	opAppend, _ = hex.DecodeString("7f353d55")
	opMul, _    = hex.DecodeString("58931c46")
)
